mapper.insert:

SELECT plaza_seq.NEXTVAL FROM dual

INSERT INTO plaza (
  plaza_id, title, content, reg_date, is_author_public, user_id,
  personal_info_consent, terms_agreement, plaza_category_id, status, idea_offer_agreement
) VALUES (
  #{plazaId}, #{title}, #{content}, SYSDATE, #{isAuthorPublic}, #{userId},
  #{personalInfoConsent}, #{termsAgreement}, #{plazaCategoryId}, 0, #{ideaOfferAgreement}
)

mapper.update:
UPDATE consulting
SET status = #{status}
WHERE consulting_id = #{consultingId}

mapper.select:
SELECT plaza_id, title, plaza_category_id, content, status, SUBSTR(user_id, 1, 3) || RPAD('*', LENGTH(user_id) - 3, '*') user_id FROM plaza
AND plaza_category_id = #{category}
ORDER BY plaza_id DESC

mapper.update:
UPDATE plaza
SET status = #{status}
WHERE plaza_id = #{plazaId}

mapper.select:
SELECT COUNT(1)
FROM plaza_like
WHERE plaza_id = #{plazaId}
AND user_id  = #{userId}
